INSERT INTO reviews (book_id, user_id, rating, title, content) VALUES
                                                                   (1, 1, 5, 'Великое произведение', 'Глубокий роман, раскрывающий человеческую природу в условиях войны.'),
                                                                   (2, 2, 4, 'Сильная психологическая драма', 'Потрясающее исследование человеческой совести и морали.'),
                                                                   (3, 3, 5, 'Шедевр драматургии', 'Тонкая лирика и глубокий символизм. Чехов в лучшей форме.'),
                                                                   (4, 4, 5, 'Поэтический гений', 'Бессмертное произведение, которое должен прочитать каждый.'),
                                                                   (5, 5, 5, 'Мистика и реальность', 'Уникальное сочетание мистики, сатиры и философии.'),
                                                                   (6, 1, 4, 'Пророческое видение', 'Пугающе актуально в наше время.'),
                                                                   (7, 2, 5, 'Важное предупреждение', 'Книга, которая заставляет задуматься о ценности знаний.'),
                                                                   (8, 3, 4, 'Простая и глубокая история', 'Минималистичный, но очень мощный рассказ о человеческом духе.'),
                                                                   (9, 4, 4, 'Классический детектив', 'Блестящий сюжет с неожиданной развязкой.'),
                                                                   (10, 5, 5, 'Волшебное приключение', 'Книга, которая покорила сердца миллионов читателей.');
